import React, { useEffect, useState } from 'react';
import { fetchNotes, deleteNote, batchDeleteNotes, editNote } from '../api';

export default function NotesList({ token }) {
    const [notes, setNotes] = useState([]);
    const [msg, setMsg] = useState('');
    const [selected, setSelected] = useState([]);
    const [editId, setEditId] = useState(null);
    const [editData, setEditData] = useState({ content: '', color: '', tags: '' });

    useEffect(() => {
        fetchNotes(token).then(setNotes).catch(() => setMsg('获取失败'));
    }, [token]);

    const handleDelete = async id => {
        if (!window.confirm('确定删除？')) return;
        await deleteNote(token, id);
        setNotes(notes.filter(n => n.id !== id));
    };

    const handleSelect = id => {
        setSelected(selected.includes(id) ? selected.filter(i => i !== id) : [...selected, id]);
    };

    const handleBatchDelete = async () => {
        if (!window.confirm('确定批量删除？')) return;
        await batchDeleteNotes(token, selected);
        setNotes(notes.filter(n => !selected.includes(n.id)));
        setSelected([]);
    };

    const startEdit = note => {
        setEditId(note.id);
        setEditData({ content: note.content, color: note.color, tags: note.tags });
    };

    const handleEditChange = e => {
        setEditData({ ...editData, [e.target.name]: e.target.value });
    };

    const handleEditSave = async () => {
        await editNote(token, editId, editData);
        setNotes(notes.map(n => n.id === editId ? { ...n, ...editData } : n));
        setEditId(null);
    };

    return (
        <div className="container mt-4">
            <h3>我的笔记</h3>
            {msg && <div className="text-danger">{msg}</div>}
            <button className="btn btn-danger mb-2" disabled={!selected.length} onClick={handleBatchDelete}>批量删除</button>
            <table className="table table-bordered">
                <thead>
                    <tr>
                        <th><input type="checkbox" checked={selected.length === notes.length && notes.length > 0} onChange={e => setSelected(e.target.checked ? notes.map(n => n.id) : [])} /></th>
                        <th>内容</th><th>URL</th><th>颜色</th><th>标签</th><th>时间</th><th>操作</th>
                    </tr>
                </thead>
                <tbody>
                    {notes.map(n => (
                        <tr key={n.id}>
                            <td><input type="checkbox" checked={selected.includes(n.id)} onChange={() => handleSelect(n.id)} /></td>
                            {editId === n.id ? (
                                <>
                                    <td><input name="content" value={editData.content} onChange={handleEditChange} className="form-control" /></td>
                                    <td><a href={n.url} target="_blank" rel="noreferrer">链接</a></td>
                                    <td><input name="color" value={editData.color} onChange={handleEditChange} className="form-control" /></td>
                                    <td><input name="tags" value={editData.tags} onChange={handleEditChange} className="form-control" /></td>
                                    <td>{n.createdAt}</td>
                                    <td>
                                        <button className="btn btn-success btn-sm me-1" onClick={handleEditSave}>保存</button>
                                        <button className="btn btn-secondary btn-sm" onClick={() => setEditId(null)}>取消</button>
                                    </td>
                                </>
                            ) : (
                                <>
                                    <td>{n.content}</td>
                                    <td><a href={n.url} target="_blank" rel="noreferrer">链接</a></td>
                                    <td><span style={{ color: n.color }}>{n.color}</span></td>
                                    <td>{n.tags}</td>
                                    <td>{n.createdAt}</td>
                                    <td>
                                        <button className="btn btn-primary btn-sm me-1" onClick={() => startEdit(n)}>编辑</button>
                                        <button className="btn btn-danger btn-sm" onClick={() => handleDelete(n.id)}>删除</button>
                                    </td>
                                </>
                            )}
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
}
