import React, { useEffect, useState } from 'react';
import { fetchNotes, deleteNote, batchDeleteNotes, editNote, exportNotes } from '../api';

export default function NotesList({ token }) {
    const [notes, setNotes] = useState([]);
    const [msg, setMsg] = useState('');
    const [selected, setSelected] = useState([]);
    const [editId, setEditId] = useState(null);
    const [editData, setEditData] = useState({ content: '', color: '', tags: '' });
    const [searchParams, setSearchParams] = useState({});
    const [pagination, setPagination] = useState({
        currentPage: 1,
        totalPages: 1,
        totalNotes: 0,
        limit: 12
    });

    const loadNotes = async (page = 1, search = '') => {
        try {
            const query = `page=${page}&limit=${pagination.limit}${search ? '&' + search : ''}`;
            const response = await fetchNotes(token, query);

            const data = response.data || response;
            const total = response.total || (Array.isArray(response) ? response.length : 0);

            setNotes(Array.isArray(data) ? data : []);
            setPagination({
                ...pagination,
                currentPage: page,
                totalPages: Math.ceil(total / pagination.limit),
                totalNotes: total
            });
        } catch (error) {
            console.error('加载笔记失败:', error);
            setMsg('获取失败');
        }
    };

    useEffect(() => {
        loadNotes(1);
    }, [token]);

    const handleDelete = async id => {
        if (!window.confirm('确定删除？')) return;
        await deleteNote(token, id);
        loadNotes(pagination.currentPage);
    };

    const handleSelect = id => {
        setSelected(selected.includes(id) ? selected.filter(i => i !== id) : [...selected, id]);
    };

    const handleBatchDelete = async () => {
        if (!window.confirm('确定批量删除？')) return;
        await batchDeleteNotes(token, selected);
        setSelected([]);
        loadNotes(1);
    };

    const startEdit = note => {
        setEditId(note.id);
        setEditData({ content: note.content, color: note.color, tags: note.tags });
    };

    const handleEditChange = e => {
        setEditData({ ...editData, [e.target.name]: e.target.value });
    };

    const handleEditSave = async () => {
        await editNote(token, editId, editData);
        loadNotes(pagination.currentPage);
        setEditId(null);
    };

    const handleSearch = e => {
        e.preventDefault();
        const input = e.target.elements.search.value.trim();
        const query = input ? `keyword=${encodeURIComponent(input)}` : '';
        loadNotes(1, query);
    };

    const handlePageChange = (newPage) => {
        if (newPage >= 1 && newPage <= pagination.totalPages) {
            loadNotes(newPage);
        }
    };

    // 格式化日期时间
    const formatDateTime = (dateStr) => {
        const date = new Date(dateStr);
        return date.toLocaleString('zh-CN', {
            year: 'numeric',
            month: '2-digit',
            day: '2-digit',
            hour: '2-digit',
            minute: '2-digit',
            hour12: false
        });
    };

    return (
        <div className="notes-container">
            <div className="notes-header">
                <div className="notes-title">
                    <h2>我的笔记</h2>
                    <span className="notes-count">{pagination.totalNotes}</span>
                </div>
                {msg && <div className="alert alert-danger">{msg}</div>}
                <div className="notes-actions">
                    <div className="search-box">
                        <form onSubmit={handleSearch}>
                            <div className="search-input-wrapper">
                                <svg className="search-icon" viewBox="0 0 24 24" width="18" height="18">
                                    <path fill="currentColor" d="M15.5 14h-.79l-.28-.27a6.5 6.5 0 1 0-.7.7l.27.28v.79l5 4.99L20.49 19l-4.99-5zm-6 0A4.5 4.5 0 1 1 14 9.5 4.5 4.5 0 0 1 9.5 14z" />
                                </svg>
                                <input
                                    name="search"
                                    type="text"
                                    className="search-input"
                                    placeholder="搜索内容、标签或网址..."
                                />
                            </div>
                        </form>
                    </div>
                    <div className="action-buttons">
                        <button
                            className={`btn-batch-delete ${!selected.length ? 'disabled' : ''}`}
                            disabled={!selected.length}
                            onClick={handleBatchDelete}
                        >
                            <svg viewBox="0 0 24 24" width="16" height="16">
                                <path fill="currentColor" d="M6 19c0 1.1.9 2 2 2h8c1.1 0 2-.9 2-2V7H6v12zM19 4h-3.5l-1-1h-5l-1 1H5v2h14V4z" />
                            </svg>
                            批量删除
                        </button>
                        <button
                            className="btn-export"
                            onClick={() => exportNotes(searchParams)}
                        >
                            <svg viewBox="0 0 24 24" width="16" height="16">
                                <path fill="currentColor" d="M19 9h-4V3H9v6H5l7 7 7-7zM5 18v2h14v-2H5z" />
                            </svg>
                            导出笔记
                        </button>
                    </div>
                </div>
            </div>

            <div className="notes-grid">
                {notes.map(note => (
                    <div key={note.id} className="note-card">
                        <div className="note-card-header">
                            <div className="note-checkbox">
                                <input
                                    type="checkbox"
                                    checked={selected.includes(note.id)}
                                    onChange={() => handleSelect(note.id)}
                                />
                            </div>
                            <div className="note-color" style={{ backgroundColor: note.color }}></div>
                        </div>

                        {editId === note.id ? (
                            <div className="note-edit-form">
                                <textarea
                                    name="content"
                                    value={editData.content}
                                    onChange={handleEditChange}
                                    className="edit-textarea"
                                    placeholder="输入笔记内容..."
                                />
                                <div className="edit-fields">
                                    <div className="edit-field">
                                        <label>颜色</label>
                                        <input
                                            type="text"
                                            name="color"
                                            value={editData.color}
                                            onChange={handleEditChange}
                                            className="edit-input"
                                        />
                                    </div>
                                    <div className="edit-field">
                                        <label>标签</label>
                                        <input
                                            type="text"
                                            name="tags"
                                            value={editData.tags}
                                            onChange={handleEditChange}
                                            className="edit-input"
                                        />
                                    </div>
                                </div>
                                <div className="edit-actions">
                                    <button className="btn-save" onClick={handleEditSave}>
                                        <svg viewBox="0 0 24 24" width="16" height="16">
                                            <path fill="currentColor" d="M9 16.17L4.83 12l-1.42 1.41L9 19 21 7l-1.41-1.41z" />
                                        </svg>
                                        保存
                                    </button>
                                    <button className="btn-cancel" onClick={() => setEditId(null)}>
                                        <svg viewBox="0 0 24 24" width="16" height="16">
                                            <path fill="currentColor" d="M19 6.41L17.59 5 12 10.59 6.41 5 5 6.41 10.59 12 5 17.59 6.41 19 12 13.41 17.59 19 19 17.59 13.41 12z" />
                                        </svg>
                                        取消
                                    </button>
                                </div>
                            </div>
                        ) : (
                            <div className="note-content">
                                <div className="note-text">
                                    {note.content.length > 100 ? note.content.slice(0, 100) + '...' : note.content}
                                </div>
                                <div className="note-meta">
                                    {note.tags && (
                                        <div className="note-tags">
                                            {note.tags.split(',').map((tag, index) => (
                                                <span key={index} className="tag">{tag.trim()}</span>
                                            ))}
                                        </div>
                                    )}
                                    <div className="note-time">
                                        {formatDateTime(note.createdAt)}
                                    </div>
                                    <a href={note.url} target="_blank" rel="noreferrer" className="note-source">
                                        <svg viewBox="0 0 24 24" width="14" height="14">
                                            <path fill="currentColor" d="M19 19H5V5h7V3H5a2 2 0 0 0-2 2v14a2 2 0 0 0 2 2h14a2 2 0 0 0 2-2v-7h-2v7zM14 3v2h3.59l-9.83 9.83 1.41 1.41L19 6.41V10h2V3h-7z" />
                                        </svg>
                                        查看来源
                                    </a>
                                </div>
                                <div className="note-actions">
                                    <button className="btn-edit" onClick={() => startEdit(note)}>
                                        <svg viewBox="0 0 24 24" width="16" height="16">
                                            <path fill="currentColor" d="M3 17.25V21h3.75L17.81 9.94l-3.75-3.75L3 17.25zM20.71 7.04a.996.996 0 0 0 0-1.41l-2.34-2.34a.996.996 0 0 0-1.41 0l-1.83 1.83 3.75 3.75 1.83-1.83z" />
                                        </svg>
                                        编辑
                                    </button>
                                    <button className="btn-delete" onClick={() => handleDelete(note.id)}>
                                        <svg viewBox="0 0 24 24" width="16" height="16">
                                            <path fill="currentColor" d="M6 19c0 1.1.9 2 2 2h8c1.1 0 2-.9 2-2V7H6v12zM19 4h-3.5l-1-1h-5l-1 1H5v2h14V4z" />
                                        </svg>
                                        删除
                                    </button>
                                </div>
                            </div>
                        )}
                    </div>
                ))}
            </div>

            {pagination.totalPages > 1 && (
                <div className="pagination">
                    <button
                        className={`page-btn ${pagination.currentPage === 1 ? 'disabled' : ''}`}
                        onClick={() => handlePageChange(pagination.currentPage - 1)}
                        disabled={pagination.currentPage === 1}
                    >
                        <svg viewBox="0 0 24 24" width="16" height="16">
                            <path fill="currentColor" d="M15.41 7.41L14 6l-6 6 6 6 1.41-1.41L10.83 12z" />
                        </svg>
                        上一页
                    </button>
                    <div className="page-info">
                        第 {pagination.currentPage} 页，共 {pagination.totalPages} 页
                    </div>
                    <button
                        className={`page-btn ${pagination.currentPage === pagination.totalPages ? 'disabled' : ''}`}
                        onClick={() => handlePageChange(pagination.currentPage + 1)}
                        disabled={pagination.currentPage === pagination.totalPages}
                    >
                        下一页
                        <svg viewBox="0 0 24 24" width="16" height="16">
                            <path fill="currentColor" d="M8.59 16.59L10 18l6-6-6-6-1.41 1.41L13.17 12z" />
                        </svg>
                    </button>
                </div>
            )}
        </div>
    );
}
