const API = 'http://localhost:3001/api';

export async function login(username, password) {
    const res = await fetch(`${API}/auth/login`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ username, password })
    });
    if (!res.ok) throw new Error('登录失败');
    return res.json();
}

export async function register(username, email, password) {
    const res = await fetch(`${API}/auth/register`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ username, email, password })
    });
    if (!res.ok) throw new Error('注册失败');
    return res.json();
}

export async function fetchNotes(token, params = '') {
    // 如果有参数，使用search接口，否则使用普通获取接口
    let url = `${API}/notes${params ? '/search' : ''}`;
    if (params && params.trim()) {
        url = `${url}?${params}`;
    }
    const res = await fetch(url, {
        headers: { 'Authorization': `Bearer ${token}` }
    });
    if (!res.ok) throw new Error('获取笔记失败');
    return res.json();
}

export async function deleteNote(token, id) {
    const res = await fetch(`${API}/notes/${id}`, {
        method: 'DELETE',
        headers: { 'Authorization': `Bearer ${token}` }
    });
    if (!res.ok) throw new Error('删除失败');
    return res.json();
}

export async function batchDeleteNotes(token, ids) {
    const res = await fetch(`${API}/notes/batch-delete`, {
        method: 'POST',
        headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json'
        },
        body: JSON.stringify({ ids })
    });
    if (!res.ok) throw new Error('批量删除失败');
    return res.json();
}

export async function editNote(token, id, data) {
    const res = await fetch(`${API}/notes/${id}`, {
        method: 'PUT',
        headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json'
        },
        body: JSON.stringify(data)
    });
    if (!res.ok) throw new Error('编辑失败');
    return res.json();
}

export async function exportNotes(params = {}, selectedIds = null) {
    const token = localStorage.getItem('webnotebook_token');
    let query = Object.entries(params)
        .map(([k, v]) => v ? `${encodeURIComponent(k)}=${encodeURIComponent(v)}` : '')
        .filter(Boolean)
        .join('&');

    // 如果有选中的笔记ID，添加到查询参数中
    if (selectedIds && Array.isArray(selectedIds) && selectedIds.length > 0) {
        const selectedIdsStr = selectedIds.join(',');
        query = query ? `${query}&selectedIds=${selectedIdsStr}` : `selectedIds=${selectedIdsStr}`;
    }

    const url = `${API}/notes/export${query ? '?' + query : ''}`;
    const res = await fetch(url, {
        headers: { 'Authorization': token ? `Bearer ${token}` : '' }
    });
    if (!res.ok) throw new Error('导出失败');

    // 获取文件名
    const contentDisposition = res.headers.get('content-disposition');
    const filename = contentDisposition ? contentDisposition.split('filename=')[1] : 'notes.xlsx';

    const blob = await res.blob();
    // 创建下载链接
    const link = document.createElement('a');
    link.href = window.URL.createObjectURL(blob);
    link.download = filename;
    link.click();
    window.URL.revokeObjectURL(link.href);
}
