const API = 'http://localhost:3001/api';

export async function login(username, password) {
    const res = await fetch(`${API}/auth/login`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ username, password })
    });
    if (!res.ok) throw new Error('登录失败');
    return res.json();
}

export async function register(username, email, password) {
    const res = await fetch(`${API}/auth/register`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ username, email, password })
    });
    if (!res.ok) throw new Error('注册失败');
    return res.json();
}

export async function fetchNotes(token, params = '') {
    // 如果有参数，使用search接口，否则使用普通获取接口
    let url = `${API}/notes${params ? '/search' : ''}`;
    if (params && params.trim()) {
        url = `${url}?${params}`;
    }
    const res = await fetch(url, {
        headers: { 'Authorization': `Bearer ${token}` }
    });
    if (!res.ok) throw new Error('获取笔记失败');
    return res.json();
}

export async function deleteNote(token, id) {
    const res = await fetch(`${API}/notes/${id}`, {
        method: 'DELETE',
        headers: { 'Authorization': `Bearer ${token}` }
    });
    if (!res.ok) throw new Error('删除失败');
    return res.json();
}

export async function batchDeleteNotes(token, ids) {
    const res = await fetch(`${API}/notes/batch-delete`, {
        method: 'POST',
        headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json'
        },
        body: JSON.stringify({ ids })
    });
    if (!res.ok) throw new Error('批量删除失败');
    return res.json();
}

export async function editNote(token, id, data) {
    const res = await fetch(`${API}/notes/${id}`, {
        method: 'PUT',
        headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json'
        },
        body: JSON.stringify(data)
    });
    if (!res.ok) throw new Error('编辑失败');
    return res.json();
}

export const exportNotes = async (selectedIds = '') => {
    try {
        const token = getToken();
        const response = await fetch(
            `${API_BASE_URL}/api/notes/export${selectedIds ? `?ids=${selectedIds}` : ''}`,
            {
                method: 'GET',
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            }
        );

        if (!response.ok) {
            const error = await response.json();
            throw new Error(error.message || '导出失败');
        }

        const blob = await response.blob();
        const url = window.URL.createObjectURL(blob);
        const a = document.createElement('a');
        const timestamp = new Date().toISOString().replace(/[-:]/g, '').split('.')[0];
        const notesCount = selectedIds ? selectedIds.split(',').length : '全部';
        const filename = `笔记导出_${notesCount}条_${timestamp}.xlsx`;

        a.href = url;
        a.download = filename;
        document.body.appendChild(a);
        a.click();
        window.URL.revokeObjectURL(url);
        document.body.removeChild(a);

        return true;
    } catch (error) {
        console.error('导出笔记失败:', error);
        throw error;
    }
};
