import React, { useState, useEffect } from 'react';
import { BrowserRouter, Routes, Route, Link } from 'react-router-dom';
import Login from './pages/Login';
import Register from './pages/Register';
import NotesList from './pages/NotesList';

export default function App() {
    const [token, setToken] = useState(localStorage.getItem('webnotebook_token') || '');

    // 监听扩展登录广播
    useEffect(() => {
        const handleStorageChange = (e) => {
            if (e.key === 'webnotebook_broadcast_login') {
                const data = JSON.parse(e.newValue);
                if (data && data.token) {
                    setToken(data.token);
                    localStorage.setItem('webnotebook_token', data.token);
                }
            } else if (e.key === 'webnotebook_broadcast_logout') {
                setToken('');
                localStorage.removeItem('webnotebook_token');
            }
        };

        window.addEventListener('storage', handleStorageChange);
        return () => window.removeEventListener('storage', handleStorageChange);
    }, []);

    const handleLogin = t => {
        setToken(t);
        localStorage.setItem('webnotebook_token', t);
        // 广播登录消息到扩展
        localStorage.setItem('webnotebook_broadcast_login', JSON.stringify({
            token: t,
            timestamp: Date.now()
        }));
    };

    const handleLogout = () => {
        setToken('');
        localStorage.removeItem('webnotebook_token');
        // 广播登出消息到扩展
        localStorage.setItem('webnotebook_broadcast_logout', JSON.stringify({
            timestamp: Date.now()
        }));
    };

    return (
        <BrowserRouter>
            <nav className="navbar navbar-expand navbar-light bg-light">
                <div className="container">
                    <Link className="navbar-brand" to="/">网页笔记本后台</Link>
                    <div>
                        {token
                            ? <button className="btn btn-outline-danger" onClick={handleLogout}>退出</button>
                            : <Link className="btn btn-outline-primary" to="/login">登录</Link>
                        }
                        <Link className="btn btn-outline-success ms-2" to="/register">注册</Link>
                    </div>
                </div>
            </nav>
            <Routes>
                <Route path="/" element={token ? <NotesList token={token} /> : <Login onLogin={handleLogin} />} />
                <Route path="/login" element={<Login onLogin={handleLogin} />} />
                <Route path="/register" element={<Register />} />
            </Routes>
        </BrowserRouter>
    );
}
